//Codice eseguito al momento del require dal main 
console.log("Codice scritto direttamente"); 

//Esposizione di una funzione in forma anonima -> Va fatto sempre PRIMA dell'esportazione delle funzioni esplicite
//Un modulo può esporre una sola funzione in forma anonima -> Si richiama con modulo()
function _anonima (){
    console.log("Funzione Anonima");
   };
   module.exports = _anonima; //Esportazione senza dare il nome alla funzione (il nome _anonima rimane interno a modulo)

   //Esposizione di funzioni esplicite
   function _somma(a, b) {
    return a + b; }
   var _moltiplicazione = function(a, b) {
    return a * b; }
    //Esportazione di funzioni esplicite accodando il nome che voglio assegnare alla funzione a module.exports
   module.exports.somma = _somma;
   module.exports.moltiplicazione = _moltiplicazione;

   //Esposizione di un json 
   let _json = {
    "nome":"Pippo",
    "setNome" :function(s) { this.nome = s }
    };
    module.exports.json = _json; //Esportazione con il solito metodo

    //Esposizione di una classe ES6
    class MyClass {
        constructor() {
        this.nome="Pippo"; //Crea la property e gli assegna un valore
        }
        setNome(s) { this.nome = s } //Metodo che permette di modificare il parametro
       }
       let myClass = new MyClass(); //istanza della classe 
       module.exports.myClass = myClass; // esporto la classe già istanziata 